import React from 'react';
import './App.css';
import 'h8k-components';
import _ from 'lodash';

import Articles from './components/Articles';

const title = "Sorting Articles";

function App({articles}) {
    const [articlesProp, setArticle] = React.useState(articles)
    const upvoteClick = () => {
        let dummyArticle = [...articlesProp]
        setArticle([])
        const sorted = _.sortBy(dummyArticle, 'upvotes').reverse();
        setArticle(sorted)
    }
    const recentClick = () => {
        let dummyArticle = [...articlesProp]
        setArticle([])
        const sorted = _.sortBy(dummyArticle, 'date').reverse();
        setArticle(sorted)
    }
    return (
        <div className="App">
            <h8k-navbar header={title}></h8k-navbar>
            <div className="layout-row align-items-center justify-content-center my-20 navigation">
                <label className="form-hint mb-0 text-uppercase font-weight-light">Sort By</label>
                <button data-testid="most-upvoted-link" className="small" onClick={upvoteClick}>Most Upvoted</button>
                <button data-testid="most-recent-link" className="small" onClick={recentClick}>Most Recent</button>
            </div>
            <Articles articles={articlesProp}/>
        </div>
    );

}

export default App;
